mapper.select:
SELECT
	IFNULL(TAGS ,'') AS TAGS
FROM BOARD_TAGS
WHERE 1=1
  AND CONTENTS_TYPE = #{contentsType}
  AND CONTENTS_ID = #{contentsId}

SELECT
	(
		SELECT
			A.ID
		FROM BOARD_LIKE A
		WHERE 1=1
		  AND A.CONTENTS_TYPE = #{contentsType}
		  AND A.CONTENTS_ID = #{contentsId}
		  AND A.LOGIN_ID = #{loginId}
		  AND A.LIKE_TYPE = '01'
		  AND A.CANCEL_YN = 'N'
		ORDER BY A.REG_DATE DESC
		LIMIT 1
	) AS LIKE_ID,
	(
		SELECT IF(COUNT(B.ID)>0,'Y','N')  FROM BOARD_LIKE B WHERE 1=1
		AND B.CONTENTS_TYPE = #{contentsType}
		AND B.CONTENTS_ID = #{contentsId}
		AND B.LOGIN_ID = #{loginId}
		AND B.LIKE_TYPE = '01'
		AND B.CANCEL_YN = 'N'
	)  AS LIKE_YN
FROM DUAL

mapper.update:
UPDATE BOARD_LIKE SET
	 CANCEL_YN = IF(CANCEL_YN='N','Y','N')
	,UPT_ID = #{uptId}
	,UPT_DATE = NOW()
WHERE ID=#{likeId}

mapper.insert:
INSERT INTO VIEWS SET
	  CONTENTS_TYPE = #{contentsType}
	, CONTENTS_ID = #{contentsId}
	, VIEW_CNT = 1
	, REG_DATE = NOW()
ON DUPLICATE KEY UPDATE
	  CONTENTS_TYPE = #{contentsType}
	, CONTENTS_ID = #{contentsId}
	, VIEW_CNT = VIEW_CNT+1
	, UPT_DATE = NOW()

mapper.update:
UPDATE BOARD_TAGS SET
TAGS=#{tags},
UPT_ID=#{uptId}, UPT_DATE=NOW() WHERE 1=1 AND CONTENTS_ID=#{contentsId} AND CONTENTS_TYPE=#{contentsType}
