mapper.select:
SELECT
	IFNULL(TAGS ,'') AS TAGS
FROM BOARD_TAGS
WHERE 1=1
  AND CONTENTS_TYPE = #{contentsType}
  AND CONTENTS_ID = #{contentsId}

SELECT
    ID AS LIKE_ID,
	IF(COUNT(*)>0,'Y','N') AS LIKE_YN
FROM BOARD_LIKE
WHERE 1=1
	AND CONTENTS_TYPE = #{contentsType}
	AND CONTENTS_ID = #{contentsId}
	AND MEMBER_ID = #{memberId}
	AND CANCEL_YN = 'N'

mapper.update:
UPDATE BOARD_LIKE SET
	CANCEL_YN = IF(CANCEL_YN='N','Y','N')
	UPT_ID = #{uptId}
	UPT_DATE = NOW()
WHERE ID=#{likeId}

UPDATE COMMENT SET
	COMENT = #{coment}
	UPT_ID = #{uptId}
	UPT_DATE = NOW()
WHERE ID=#{commentId}

UPDATE COMMENT SET
	DELETE_YN = 'Y'
	UPT_ID = #{uptId}
	UPT_DATE = NOW()
WHERE ID=#{commentId}

mapper.insert:
INSERT INTO VIEWS SET
	  CONTENTS_TYPE = #{contentsType}
	, CONTENTS_ID = #{contentsId}
	, VIEW_CNT = 1
	, REG_DATE = NOW()
ON DUPLICATE KEY UPDATE
	  CONTENTS_TYPE = #{contentsType}
	, CONTENTS_ID = #{contentsId}
	, VIEW_CNT = VIEW_CNT+1
	, UPT_DATE = NOW()
