mapper.select:
/*category > member_category_list*/ SELECT ID ,REG_ID ,DATE_FORMAT(REG_DATE, '%Y.%m.%d %H:%i:%s') AS REG_DATE ,UPT_ID ,DATE_FORMAT(UPT_DATE, '%Y.%m.%d %H:%i:%s') AS UPT_DATE ,DEFALUT_CATEG_ID ,LOGIN_ID ,CATEGORY_NM ,CATEGORY_COMENTS ,CATEGORY_IMG_PATH ,SHOW_YN ,DELETE_YN ,'member' AS CATEGORY_TYPE FROM MEM_DEFINE_MENU_CATEG WHERE 1=1 AND DELETE_YN = 'N'
AND LOGIN_ID IN (SELECT LOGIN_ID FROM MEMBER WHERE ID = #{memId})
AND SHOW_YN = #{showYn}
