mapper.select:
SELECT MM.ID , MM.REG_ID , DATE_FORMAT(MM.REG_DATE,'%Y-%m-%d %H:%i') AS REG_DATE , MM.UPT_ID , DATE_FORMAT(MM.UPT_DATE,'%Y-%m-%d %H:%i') AS UPT_DATE , MM.LOGIN_ID , MM.COMMENT_PERM_STATUS , MM.STORY_REG_PERM_STATUS , MM.STORY_COMMENT_PUBLIC_STATUS , MM.STORY_TITLE , M.MEM_PROFILE_IMG FROM MEMBER_MANAGEMENT MM INNER JOIN MEMBER M ON M.LOGIN_ID = MM.LOGIN_ID
AND MM.LOGIN_ID = #{loginId}
AND EXISTS (SELECT LOGIN_ID FROM MEMBER WHERE ID= #{id} AND LOGIN_ID = MM.LOGIN_ID)
