	, NAME
	, NICKNAME
	, MEM_TYPE
	, MEM_PROFILE_IMG
	, REG_ID
	, REG_DATE
	, UPT_ID
	, UPT_DATE
)
VALUES (
	  #{loginId}
	, #{password}
	, #{email}
	, #{phoneNum}
	, #{gender}
	, #{birth}
	, #{name}
	, #{nickname}
	, #{memType}
	, #{memProfileImg}
	, #{loginId}
	, NOW()
	, #{loginId}
	, NOW()
)
ON DUPLICATE KEY UPDATE

		PASSWORD 	= #{password} ,



		EMAIL 		= #{email} ,



		PHONE_NUM 	= #{phoneNum} ,



		NAME 		= #{name} ,



		NICKNAME 	= #{nickname} ,



		MEM_TYPE 	= #{memType} ,


	UPT_ID 	= #{loginId} ,
	UPT_DATE 	= NOW()
